parameters.inputSchema:
{
    "type": "object",
    "properties": {
        "search_summary": {
            "type": "string",
            "description": "Detailed summary of the search query and results overview"
        },
        "total_listings_found": {
            "type": "integer",
            "description": "Total number of listings returned"
        },
        "search_location": {
            "type": "string",
            "description": "Primary search location/destination"
        },
        "guest_count": {
            "type": "integer",
            "description": "Number of guests the search was for"
        },
        "price_range": {
            "type": "string",
            "description": "Price range of all listings (e.g., '$489 - $879 USD for 5 nights')"
        },
        "listings": {
            "type": "array",
            "items": {
                "type": "object",
                "properties": {
                    "property_name": {
                        "type": "string",
                        "description": "Property/listing name or title"
                    },
                    "property_link": {
                        "type": "string",
                        "description": "Direct Airbnb listing URL"
                    },
                    "location_details": {
                        "type": "string",
                        "description": "Specific location (neighborhood, area, city)"
                    },
                    "property_type": {
                        "type": "string",
                        "description": "Type of accommodation (villa, private room, entire home, etc.)"
                    },
                    "standout_features": {
                        "type": "string",
                        "description": "Key features that make this property special (pool, beach access, views, etc.)"
                    },
                    "why_recommended": {
                        "type": "string",
                        "description": "Detailed reasons why this property matches the user's search criteria. Please add as many details as possible"
                    },
                    "value_assessment": {
                        "type": "string",
                        "enum": ["Excellent Value", "Good Value", "Premium Pricing", "Budget Option"],
                        "description": "Value for money assessment"
                    },
                    "guest_rating": {
                        "type": "number",
                        "description": "Average guest rating out of 5.0"
                    },
                    "review_count": {
                        "type": "integer",
                        "description": "Total number of reviews"
                    },
                    "total_price": {
                        "type": "string",
                        "description": "Total price for the stay period"
                    },
                    "cancellation_policy": {
                        "type": "string",
                        "description": "Cancellation policy details"
                    },
                    "booking_urgency": {
                        "type": "string",
                        "enum": ["High", "Medium", "Low"],
                        "description": "Recommended booking urgency based on availability and demand"
                    },
                    "target_traveler": {
                        "type": "string",
                        "description": "Who this property is best suited for (couples, families, solo travelers, etc.)"
                    }
                },
                "required": [
                    "property_name",
                    "property_link",
                    "location_details",
                    "property_type",
                    "standout_features",
                    "why_recommended",
                    "value_assessment",
                    "guest_rating",
                    "review_count",
                    "total_price",
                    "cancellation_policy",
                    "booking_urgency",
                    "target_traveler"
                ]
            }
        },
        "recommendation_summary": {
            "type": "string",
            "description": "Overall recommendation and next steps for the traveler"
        },
        "additional_search_link": {
            "type": "string",
            "description": "Link to browse more options on Airbnb"
        }
    },
    "required": [
        "search_summary",
        "total_listings_found", 
        "search_location",
        "guest_count",
        "price_range",
        "listings",
        "recommendation_summary"
    ]
}

